export declare const queryToGetColumnDescription = "\n    SELECT \n        COLUMN_NAME AS 'Column',\n        COLUMN_TYPE AS 'Type',\n        IS_NULLABLE AS 'Nullable',\n        COLUMN_KEY AS 'Key',\n        COLUMN_DEFAULT AS 'DefaultValue',\n        EXTRA AS 'Extras',\n        COLUMN_COMMENT AS 'Comment',\n        TABLE_NAME\n    FROM \n        information_schema.columns\n    WHERE \n        TABLE_SCHEMA = ? \n";
